	public static int test(String s) {
		int count = 0;

		String[] strings = s.split("\r\n");
		for (String string : strings) {

			boolean containsPairTwice = false;
			for (int i = 0; i < string.length() - 3; ++i) {
				for (int j = i + 2; j < string.length() - 1; ++j) {
					if (string.charAt(i) == string.charAt(j)
							&& string.charAt(i + 1) == string.charAt(j + 1)) {
						containsPairTwice = true;
						break;
					}
				}
			}

			boolean containsLetterTwice = false;
			for (int i = 0; i < string.length() - 2; ++i) {
				if (string.charAt(i) == string.charAt(i + 2)) {
					containsLetterTwice = true;
					break;
				}
			}

			boolean isNice = containsPairTwice && containsLetterTwice;
			if (isNice) {
				++count;
			}
		}

		return count;
	}
