private fun test(input: String): Int {
    val figuresInput = """
        ####

        .#.
        ###
        .#.

        ..#
        ..#
        ###

        #
        #
        #
        #

        ##
        ##
    """.trimIndent()

    val figures = arrayListOf<MutableSet<Point>>()

    for (part in figuresInput.split("\n\n")) {
        val figure = mutableSetOf<Point>()
        var row = 0
        for (line in part.split("\n")) {
            var col = 0
            for (char in line) {
                if (char == '#') {
                    figure += Point(row, col)
                }
                ++col
            }
            ++row
        }
        figures += figure
    }

    val tower = mutableSetOf<Point>()
    val wellLeft = 0
    val wellRight = 8
    val wellBottom = 0

    fun Set<Point>.height() = if (isEmpty()) 0 else (maxBy { it.x }?.x ?: 0) - (minBy { it.x }?.x ?: 0) + 1

    fun Set<Point>.startPoint() = Point(wellBottom - tower.height() - 3 - height(), wellLeft + 3)

    fun Set<Point>.fitsWell() = all { it.x < wellBottom && it.y in wellLeft + 1..wellRight - 1 }

    fun Point.moveTo(start: Point) = Point(x + start.x, y + start.y)

    fun Set<Point>.moveTo(start: Point): Set<Point> {
        val result = mutableSetOf<Point>()
        for (p in this) {
            result += p.moveTo(start)
        }
        return result
    }

    fun Set<Point>.print() {
        val minX = min(minBy { it.x }?.x ?: 0, wellBottom)
        val maxX = max(maxBy { it.x }?.x ?: 0, wellBottom)
        val minY = min(min(minBy { it.y }?.y ?: 0, wellLeft), wellRight)
        val maxY = max(max(maxBy { it.y }?.y ?: 0, wellRight), wellLeft)
        for (x in minX..maxX) {
            for (y in minY..maxY) {
                val p = Point(x, y)
                print(if (p in this) '#' else if (p.x < wellBottom && (p.y == wellLeft || p.y == wellRight) || (p.x == wellBottom && p.y in wellLeft..wellRight)) 'o' else '.')
            }
            println()
        }
        println()
    }

    var moveIndex = 0
    for (figureNumber in 1..2022) {
        val figure = figures[(figureNumber - 1) % figures.size]
        val figureStartPos = figure.startPoint()
        var concreteFigure = figure.moveTo(figureStartPos)

        //println("round $figureNumber")
        //println("figure appears")
        fun printIteration() {
            val points = mutableSetOf<Point>()
            points += concreteFigure
            points += tower
            points.print()
        }
        //printIteration()

        var stopped = false
        while (!stopped) {
            //println("figure winds")
            var prevFigure = concreteFigure
            when (input[moveIndex % input.length]) {
                '<' -> concreteFigure = concreteFigure.moveTo(Point(0, -1))
                '>' -> concreteFigure = concreteFigure.moveTo(Point(0, 1))
                else -> throw IllegalStateException("invalid wind")
            }
            if (!concreteFigure.fitsWell() || concreteFigure.any { it in tower }) {
                concreteFigure = prevFigure
            }
            ++moveIndex
            //printIteration()

            //println("figure falls")
            prevFigure = concreteFigure
            concreteFigure = concreteFigure.moveTo(Point(1, 0))
            if (!concreteFigure.fitsWell() || concreteFigure.any { it in tower }) {
                concreteFigure = prevFigure
                tower += concreteFigure
                stopped = true
            }
            //printIteration()
        }
    }

    return tower.height()
}
