package advent.advent2022

private var result = 0

fun main() {
    result = test("""
        mjqjpqmgbljsphdztnvjfqwrcgsmlb
    """.trimIndent())
    check(19, result)

    result = test("""
        bvwbjplbgvbhsrlpgdmjqwftvncz
    """.trimIndent())
    check(23, result)

    result = test("""
        nppdvjthqldpwncqszvftbrmjlhg
    """.trimIndent())
    check(23, result)

    result = test("""
        nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg
    """.trimIndent())
    check(29, result)

    result = test("""
        zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw
    """.trimIndent())
    check(26, result)

    result = test("""
        
    """.trimIndent())
    check(3551, result)
}

private fun test(input: String): Int {
    for (i in 14..input.length) {
        val set = mutableSetOf<Char>()
        for (j in i - 14..i - 1) set += input[j]
        if (set.size == 14) return i
    }
    return 0
}
