    public static int test(String s) {
        int counter = 0;

        for (int i = 0; i < s.length(); ++i) {
            if (s.charAt(i) == '(') {
                ++counter;
            } else if (s.charAt(i) == ')') {
                --counter;
            }
        }

        return counter;
    }
