package advent2016;

public class AoC2016Day2Part1 {
    private static final int[] CODES = new int[]{1, 2, 3, 4, 5, 6, 7, 8, 9};

    public static void main(String[] args) {
        String result;

        result = test("ULL", "RRDDD", "LURDL", "UUUUD");
        assert result.equals("1985");
        System.out.println(result);

        result = test(
                "",
                "",
                "",
                "",
                "");
        assert result.equals("35749");
        System.out.println(result);
    }

    public static String test(String... strings) {
        String result = "";

        int x = 1, y = 1;
        for (int i = 0; i < strings.length; ++i) {
            for (int j = 0; j < strings[i].length(); ++j) {
                char ch = strings[i].charAt(j);
                switch (ch) {
                    case 'U':
                        y -= 1;
                        y = y < 0 ? 0 : y;
                        break;
                    case 'D':
                        y += 1;
                        y = y > 2 ? 2 : y;
                        break;
                    case 'L':
                        x -= 1;
                        x = x < 0 ? 0 : x;
                        break;
                    case 'R':
                        x += 1;
                        x = x > 2 ? 2 : x;
                        break;
                }
            }
            result += CODES[y * 3 + x];
        }

        return result;
    }
}