package advent2016;
public class AoC2016Day2Part2 {
	public static void main(String[] args) {
		String result;

		result = test("ULL", "RRDDD", "LURDL", "UUUUD");
		assert (result.equals("5DB3"));
		System.out.println(result);

		result = test(
				"",
				"",
				"",
				"",
				"");
		System.out.println(result);
	}

	private static final int[] CODES = new int[] { 0, 0, 1, 0, 0, 0, 2, 3, 4,
			0, 5, 6, 7, 8, 9, 0, 10, 11, 12, 0, 0, 0, 13, 0, 0 };

	public static String test(String... strings) {
		String result = "";

		int x = 0, y = 2;
		for (int i = 0; i < strings.length; ++i) {
			for (int j = 0; j < strings[i].length(); ++j) {
				char ch = strings[i].charAt(j);
				switch (ch) {
				case 'U':
					y -= 1;
					y = y < 0 ? 0 : y;
					y = CODES[y * 5 + x] == 0 ? y + 1 : y;
					break;
				case 'D':
					y += 1;
					y = y > 4 ? 4 : y;
					y = CODES[y * 5 + x] == 0 ? y - 1 : y;
					break;
				case 'L':
					x -= 1;
					x = x < 0 ? 0 : x;
					x = CODES[y * 5 + x] == 0 ? x + 1 : x;
					break;
				case 'R':
					x += 1;
					x = x > 4 ? 4 : x;
					x = CODES[y * 5 + x] == 0 ? x - 1 : x;
					break;
				}
			}
			int code = CODES[y * 5 + x];
			if (code < 10) {
				result += code;
			} else {
				switch (code) {
				case 10:
					result += 'A';
					break;
				case 11:
					result += 'B';
					break;
				case 12:
					result += 'C';
					break;
				case 13:
					result += 'D';
					break;
				}
			}
		}

		return result;
	}
}