    private static String test(String s) {
        String[] table = s.split("\n");
        int y = 0, x = 0;
        for (; y < table[x].length(); ++y) {
            if (table[0].charAt(y) == '|') {
                break;
            }
        }
        StringBuilder result = new StringBuilder();
        boolean down = true, up = false, left = false, right = false;
        while (x >= 0 && x < table.length) {
            if (y < 0 || y >= table[x].length()) {
                break;
            }
            if (table[x].charAt(y) == '+') {
                if (down || up) {
                    if (table[x].charAt(y - 1) != ' ') {
                        left = true;
                        down = false;
                        up = false;
                    } else if (table[x].charAt(y + 1) != ' ') {
                        right = true;
                        down = false;
                        up = false;
                    } else {
                        break;
                    }
                } else if (left || right) {
                    if (table[x - 1].charAt(y) != ' ') {
                        up = true;
                        left = false;
                        right = false;
                    } else if (table[x + 1].charAt(y) != ' ') {
                        down = true;
                        left = false;
                        right = false;
                    } else {
                        break;
                    }
                } else {
                    throw new RuntimeException("unreachable");
                }
            } else if (Character.isLetter(table[x].charAt(y))) {
                result.append(table[x].charAt(y));
                System.out.println(result);
            } else if (table[x].charAt(y) == ' ') {
                break;
            }
            if (down) {
                ++x;
            } else if (up) {
                --x;
            } else if (left) {
                --y;
            } else if (right) {
                ++y;
            } else {
                throw new RuntimeException("unreachable");
            }
        }
        return result.toString();
    }
