private fun test(input: String): Int {
    val cucumbers = input.split("\n").map { it.toCharArray() }.toTypedArray()

    val width = cucumbers[0].size

    var steps = 0

    var current = cucumbers
    while (true) {

        // move east
        val next = Array(current.size) { CharArray(width) }
        for (i in current.indices) {
            for (j in current[i].indices) {
                if (current[i][j] == '>') {
                    val nextJ = if (j + 1 == width) 0 else j + 1
                    if (current[i][nextJ] == '.') {
                        next[i][j] = '.'
                        next[i][nextJ] = '>'
                    } else {
                        next[i][j] = '>'
                    }
                } else {
                    if (next[i][j] != '>') next[i][j] = current[i][j]
                }
            }
        }

        // move south
        val nextnext = Array(current.size) { CharArray(width) }
        for (i in next.indices) {
            for (j in next[i].indices) {
                if (next[i][j] == 'v') {
                    val nextI = if (i + 1 == current.size) 0 else i + 1
                    if (next[nextI][j] == '.') {
                        nextnext[i][j] = '.'
                        nextnext[nextI][j] = 'v'
                    } else {
                        nextnext[i][j] = 'v'
                    }
                } else {
                    if (nextnext[i][j] != 'v') nextnext[i][j] = next[i][j]
                }
            }
        }

        ++steps
        if (current contentDeepEquals next) {
            break
        }
        current = nextnext
        println("after $steps step")
        for (i in current.indices) {
            println(current[i])
        }
        println()
    }

    return steps
}
