package advent2022

private var result = 0

fun main() {
    result = test("""
        mjqjpqmgbljsphdztnvjfqwrcgsmlb
    """.trimIndent())
    check(7, result)

    result = test("""
        bvwbjplbgvbhsrlpgdmjqwftvncz
    """.trimIndent())
    check(5, result)

    result = test("""
        nppdvjthqldpwncqszvftbrmjlhg
    """.trimIndent())
    check(6, result)

    result = test("""
        nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg
    """.trimIndent())
    check(10, result)

    result = test("""
        zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw
    """.trimIndent())
    check(11, result)

    result = test("""
        
    """.trimIndent())
    check(1802, result)
}

private fun test(input: String): Int {
    for (i in 4..input.length) {
        val set = mutableSetOf<Char>()
        for (j in i - 4..i - 1) set += input[j]
        if (set.size == 4) return i
    }
    return 0
}
